public class VariableEx {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//10을 변수 value의 초기값으로 저장
		int value = 10;
		
		//변수 value 값을 읽고 10을 더하는 산술 연산을 시행
		
		int result = value + 10;
		
		// 변수 reslut의 값을 읽고 출력
		
		System.out.println(result);
		

	}

}
